ready(()=>{const t=new IntersectionObserver(([t])=>{t.target.querySelectorAll("[data-stuck]").forEach(e=>{e.getAttribute("data-stuck").split(" ").forEach(s=>{t.intersectionRatio<1?e.classList.add(s):e.classList.remove(s)})})},{threshold:[1]});document.querySelectorAll(".sticky.top-0").forEach(e=>{e.style.top="-1px",t.observe(e),e.querySelectorAll('[class*="stuck:"]').forEach(t=>{for(var e=t.getAttribute("class").split(" "),s=[],r=0;r<e.length;r++)if(-1!==e[r].indexOf("stuck:")){t.classList.remove(e[r]);var c=e[r].split(":");c.shift(),s.push(c.join(":"))}t.setAttribute("data-stuck",s.join(" "))})})});