ready(()=>{let t=document.querySelectorAll("[data-text-scale]");if(0===t.length)return;t.forEach(t=>{let e=t.getBoundingClientRect();t.setAttribute("data-width",e.width)});let e=()=>{t.forEach(t=>{let e=t.parentNode.getBoundingClientRect();if(e.width>=t.getAttribute("data-width"))t.style.transform="scale(1)";else{let a=.94*e.width/t.getAttribute("data-width");t.style.transform="scale("+a+")"}})};window.addEventListener("resize",e),e()});
