ready(()=>{function t(){let t=document.querySelectorAll("[data-timer]");t.forEach(t=>{let e=t.getAttribute("data-timer"),$=JSON.parse(e).map(t=>new Date(t).getTime()).sort((t,e)=>t-e),r=$.find(t=>t>new Date().getTime()),o=r-new Date().getTime(),a=t.querySelector("[data-timer-days]"),n=t.querySelector("[data-timer-hours]"),i=t.querySelector("[data-timer-minutes]"),d=t.querySelector("[data-timer-seconds]");a&&(a.textContent=Math.floor(o/864e5).toString().padStart(2,"0")),n&&(n.textContent=Math.floor(o%864e5/36e5).toString().padStart(2,"0")),i&&(i.textContent=Math.floor(o%36e5/6e4).toString().padStart(2,"0")),d&&(d.textContent=Math.floor(o%6e4/1e3).toString().padStart(2,"0"))})}setInterval(t,1e3),t()});